<?php// Start session if not already startedif (session_status() === PHP_SESSION_NONE) {    session_start();}// Include your environment-aware database connectioninclude 'db_connect.php';// Initialize variables$error = '';$username_email = '';// Check if user is logged in$loggedIn = isset($_SESSION['user_id']);$user = null;// If user is logged in, fetch their informationif ($loggedIn) {         // Prepare and execute query to get user details    $stmt = $conn->prepare("SELECT id, fullname, username, email, profile_image FROM users WHERE id = ?");    $stmt->bind_param("i", $_SESSION['user_id']);    $stmt->execute();    $result = $stmt->get_result();        // If user exists, store their details    if ($result->num_rows > 0) {        $user = $result->fetch_assoc();    }        // Close statement and connection    $stmt->close();    $conn->close();}?><!DOCTYPE html><html lang="en"><head>    <meta charset="UTF-8">    <meta name="viewport" content="width=device-width, initial-scale=1.0">    <title>HirayaFit - Premium Activewear</title>    <link rel="icon" href="images/hf.png">    <link href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta3/css/all.min.css" rel="stylesheet">    <style>        :root {            --primary: #111111;            --secondary: #0071c5;            --accent: #e5e5e5;            --light: #ffffff;            --dark: #111111;            --grey: #767676;            --light-grey: #f5f5f5;            --border-color: #e0e0e0;        }        * {            margin: 0;            padding: 0;            box-sizing: border-box;            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;        }        body {            background-color: var(--light);        }        .top-bar {            background-color: var(--primary);            color: white;            padding: 8px 0;            text-align: center;            font-size: 12px;            letter-spacing: 0.5px;        }        .top-bar .container {            display: flex;   