{    bottom: 0;}.product-actions button {    background-color: white;    border: 1px solid #eee;    border-radius: 50%;    width: 40px;    height: 40px;    display: flex;    align-items: center;    justify-content: center;    cursor: pointer;    transition: all 0.3s ease;    box-shadow: 0 3px 8px rgba(0, 0, 0, 0.08);}.product-actions button:hover {    background-color: var(--secondary);    border-color: var(--secondary);    color: var(--light);    transform: translateY(-2px);}.product-info {    padding: 22px;    text-align: center;    flex-grow: 1;    display: flex;    flex-direction: column;}.product-info h3 {    margin-top: 0;    margin-bottom: 12px;    font-size: 1.1rem;    font-weight: 600;    color: var(--primary);    line-height: 1.4;}.price {    color: #e74c3c;    font-weight: bold;    font-size: 1.25rem;    margin-bottom: 12px;}.category {    color: var(--grey);    font-size: 0.85rem;    margin-bottom: 12px;    text-transform: uppercase;    letter-spacing: 0.5px;}.product-rating {    display: flex;    justify-content: center;    align-items: center;    gap: 6px;    margin-bottom: 18px;}.stars {    color: #ffaa00;    font-size: 0.9rem;}.rating-text {    color: var(--grey);    font-size: 0.85rem;}.product-buttons {    margin-top: auto;}.add-to-cart, .view-details {    background-color: var(--primary);    color: var(--light);    border: none;    padding: 12px 16px;    border-radius: 6px;    cursor: pointer;    font-weight: 600;    transition: all 0.3s ease;    margin-top: 12px;    width: 100%;    letter-spacing: 0.5px;}.view-details {    background-color: #f5f5f5;    color: #333;    margin-bottom: 8px;    border: 1px solid #eee;}.add-to-cart:hover {    background-color: var(--secondary);    transform: translateY(-2px);    box-shadow: 0 4px 10px rgba(0, 113, 197, 0.3);}.view-details:hover {    background-color: #ebebeb;    transform: translateY(-2px);    box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);}.no-results {    text-align: center;    font-size: 1.3rem;    color: #666;    padding: 40px 0;    width: 100%;    grid-column: 1 / -1;}/* Modal Styles for Quick View */.modal {    display: none;    position: fixed;    top: 0;    left: 0;    width: 100%;    height: 100%;    background-color: rgba(0, 0, 0, 0.75);    z-index: 1000;    overflow-y: auto;    backdrop-filter: blur(5px);}.modal-content {    position: relative;    background-color: white;    margin: 5% auto;    padding: 0;    width: 90%;    max-width: 1100px;    border-radius: 12px;    overflow: hidden;    box-shadow: 0 10px 40px rgba(0, 0, 0, 0.25);}.close-modal {    position: absolute;    top: 18px;    right: 22px;    font-size: 28px;    font-weight: bold;    color: #333;    cursor: pointer;    z-index: 10;    transition: all 0.2s ease;    width: 40px;    height: 40px;    display: flex;    align-items: center;    justify-content: center;    border-radius: 50%;}.close-modal:hover {    background-color: rgba(0, 0, 0, 0.05);    color: var(--secondary);}.quick-view-container {    padding: 30px;}.quick-view-grid {    display: grid;    grid-template-columns: 1fr 1fr;    gap: 40px;}@media (max-width: 900px) {    .quick-view-grid {        grid-template-columns: 1fr;        gap: 30px;    }}.quick-view-image {    display: flex;    align-items: center;    justify-content: center;    background-color: #f9f9f9;    border-radius: 8px;    padding: 20px;}.quick-view-image img {    max-width: 100%;    max-height: 500px;    object-fit: contain;    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);}.quick-view-details h2 {    margin-top: 0;    font-size: 2rem;    color: #333;    line-height: 1.3;}.quick-view-details .quick-price {    font-size: 1.5rem;    color: var(--secondary);    font-weight: bold;    margin: 15px 0;}.quick-view-details .description {    color: #555;    line-height: 1.7;    margin: 20px 0;    font-size: 1rem;}.product-options {    margin: 25px 0;}.product-options h4 {    margin-bottom: 12px;    color: #333;    font-size: 1.1rem;}.option-buttons {    display: flex;    flex-wrap: wrap;    gap: 12px;    margin-bottom: 20px;}.size-btn {    padding: 10px 15px;    border: 1px solid #ddd;    background-color: white;    cursor: pointer;    transition: all 0.3s ease;    border-radius: 4px;    font-weight: 500;}.size-btn:hover, .size-btn.active {    background-color: var(--primary);    color: var(--light);    border-color: var(--primary);}.color-btn {    width: 35px;    height: 35px;    border-radius: 50%;    border: 2px solid #ddd;    cursor: pointer;    position: relative;    overflow: hidden;    text-indent: -9999px;    transition: all 0.2s ease;}.color-btn:hover, .color-btn.active {    border: 3px solid var(--primary);    transform: scale(1.1);}.quantity-section {    margin: 25px 0;}.quantity-section h4 {    margin-bottom: 12px;    color: #333;    font-size: 1.1rem;}.quantity-control {    display: flex;    align-items: center;    max-width: 140px;    border-radius: 5px;    overflow: hidden;    border: 1px solid #ddd;}.quantity-btn {    width: 40px;    height: 40px;    background-color: #f5f5f5;    border: none;    cursor: pointer;    font-size: 1.2rem;    display: flex;    align-items: center;    justify-content: center;    transition: all 0.2s ease;}.quantity-btn:hover {    background-color: #e0e0e0;}.quantity-input {    width: 60px;    height: 40px;    text-align: center;    border: none;    border-left: 1px solid #ddd;    border-right: 1px solid #ddd;    font-size: 1rem;}.add-to-cart-modal {    background-color: var(--secondary);    color: var(--light);    border: none;    padding: 14px 25px;    font-size: 1.1rem;    font-weight: 600;    border-radius: 6px;    cursor: pointer;    transition: all 0.3s ease;    margin-top: 25px;    width: 100%;    max-width: 350px;    letter-spacing: 0.5px;    display: flex;    align-items: center;    justify-content: center;    gap: 10px;}.add-to-cart-modal:hover {    background-color: #005da3; /* Slightly darker variant of secondary */    transform: translateY(-3px);    box-shadow: 0 5px 15px rgba(0, 113, 197, 0.3);}.add-to-cart-modal i {    font-size: 1.2rem;}/* Navigation Styles for Category Links */.main-nav a[data-category] {    position: relative;}.main-nav a[data-category]:after {    content: '';    position: absolute;    width: 0;    height: 3px;    bottom: -3px;    left: 50%;    background-color: var(--secondary);    transition: all 0.3s ease;    transform: translateX(-50%);}.main-nav a[data-category]:hover:after {    width: 100%;}.main-nav a[data-category].active {    color: var(--secondary);    font-weight: 600;}.main-nav a[data-category].active:after {    width: 100%;}/* Responsive Adjustments */@media (max-width: 992px) {.products-grid {grid-template-columns: repeat(auto-fill, minmax(240px, 1fr));}}@media (max-width: 768px) {#featured {padding: 50px 0;}.section-title {font-size: 28px;}.section-description {font-size: 16px;margin-bottom: 30px;}.products-grid {grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));gap: 20px;}.product-image {height: 200px;}.quick-view-grid {grid-template-columns: 1fr;}.quick-view-image {margin-bottom: 20px;}}@media (max-width: 480px) {.products-grid {grid-template-columns: 1fr;}}   </style> </body> </html>